namespace Field\Blog\Block\Adminhtml\Comment\Edit;

class Tabs extends \Magento\Backend\Block\Widget\Tabs
{
    /**
     * @return void
     */
    protected function _construct()
    {
        parent::_construct();
        $this->setId('post_tabs');
        $this->setDestElementId('edit_form');
        $this->setTitle(__('Comment Information'));

        $this->addTab(
                'main_section',
                [
                    'label' => __('General'),
                    'content' => $this->getLayout()->createBlock('Field\Blog\Block\Adminhtml\Comment\Edit\Tab\Main')->toHtml()
                ]
            );
    }
}
